css:
@-moz-document domain("steamcn.com") {
.bbcode_spoiler_content {
	opacity: initial;
}

font[style="background-color:black"],
font[style="background-color:BLACK"],
font[style="background-color:Black"],
font[style="background-color:#000"],
font[style="background-color:#000000"],
font[style="background-color:rgb(0,0,0)"],
font[style="background-color:rgb(0, 0, 0)"],
font[style="background-color:black"] *,
font[style="background-color:BLACK"] *,
font[style="background-color:Black"] *,
font[style="background-color:#000"] *,
font[style="background-color:#000000"] *,
font[style="background-color:rgb(0,0,0)"] *,
font[style="background-color:rgb(0, 0, 0)"] * {
	color: #fff !important;
}

div.showhide p:first-child {
	display: none;
}

div.spoiler {
	display: block !important;
}

div.spoiler:before {
	display: block;
	content: "剧透内容";
	margin-bottom: 10px;
    color: #F66;
    font-size: 12px;
}
}

@-moz-document domain("steamcommunity.com"), domain("steampowered.com") {
span.bb_spoiler {
    color: #ffffff;
}

span.bb_spoiler span {
    visibility: visible;
}
}

@-moz-document domain("www.steamgifts.com") {
.markdown .spoiler {
    color: initial !important;
}
}
