css:
@-moz-document domain("docs.unrealengine.com") {
#webThree,
body#webbgThree {
    background: #3E3E3E!important;
}
#navPanel,
#navPanel ul,
li,
nav.main-menu {
    background-color: #3E3E3E!important;
}
#header>nav.main-menu ul li a {
    color: #3E3E3E;
}
li.logo {
    background: -moz-linear-gradient(left, rgba(176, 176, 176, 0.65) 0%, rgba(38, 38, 38, 1) 100%);
    /* FF3.6+ */
    
    background: -webkit-gradient(linear, left top, right top, color-stop(0%, rgba(176, 176, 176, 0.65)), color-stop(100%, rgba(38, 38, 38, 1)));
}
#navPanel li>a:visited {
    color: #3E3E3E;
}
#navPanel li>a.selectedLink {
    background-color: #3E3E3E!important;
}
/*General text/link styling */

#navPanel li>a,
li>span.index_nolink,
div.features .inner .content .description,
div.features .inner .title,
.largetitle,
.largetitle p,
.topic .wrapper .largetitle,
.topic .wrapper .largetitle p {
    color: #FFFFFF;
}
/* Page Content */

#contentContainer,
#pagedefault,
#pagecontainer,
#maincol,
#footer #brand,
#footer #copyright,
#footer #legal,
#footer #terms,
.section {
    background: #212121;
    color: #FFFFFF;
}
pre.prettyprint {
    background: #212121!important;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: inherit;
}
.navigation>.button a {
    background-color: #3E3E3E;
    color: inherit;
}
.navigation .button a:hover {
    background-color: #212121;
}
#maincol {
    border: none;
    background-color: #212121;
}
#maincol table td {
    background-color: #212121;
}
#maincol ol li,
#maincol ul li {
    background-color: #212121!important;
}
a,
a:hover,
a:visited,
a:link,
.crumbs a,
.crumbs a:hover,
.crumbs a:visited,
.crumbs a:link,
#maincol ol li a,
#maincol ul li a,
#maincol table td a {
    color: #008FFF;
}
#maincol .linkcontainer ul li a {
    background-color: #212121;
    border: 1px solid #FFFFFF;
}
#maincol .linkcontainer ul li a:hover {
    background-color: #212121;
}
.searchterm_button {
    background: #212121;
    color: #FFFFFF;
}
.tip,
.note {
    background-color: #212121;
}
/* Search */

.cse .gsc-control-cse,
.gsc-control-cse {
    background: #212121;
}
.gs-result:hover {
    background-color: #3E3E3E!important;
}
div.gsc-orderby-label,
div.gsc-selected-option-container {
    color: #3E3E3E;
}
}
