css:
@-moz-document url("https://zestedesavoir.com/") {
.flexpage .flexpage-wrapper {
    max-width:1148px;
}

.featured-resource-item {
    max-width:163px;
    min-width:140px;
}

.home .flexpage-header {
    margin-bottom:-138px;
    padding-bottom:152px;
}

.home-pinned-content {
    width:calc(163px * 5);
    margin:auto;
}

.home .featured-resource-row {
	margin-bottom:20px;   
}

.featured-resource-item a:focus .featured-resource-meta h3,
.featured-resource-item a:hover .featured-resource-meta h3 {
    height:130px;
}

.content-item .content-illu {
    -webkit-box-flex:0;
    -ms-flex:0 0 60px;
    flex:0 0 60px;
    height:60px;
}

.content-item .content-info {
 	padding:4px 8px;
    height:inherit;
}

.content-item.topic-item .content-info  {
 	padding:4px 8px;
    height:inherit;
}

.content-item .content-tags {
	display:none;
}

.content-item.topic-item .content-title,
.content-item .content-title {
    font-size:16px;
    line-height:17px;
    min-height:16px;
}

.content-item .content-description,
.content-item.topic-item .content-description {
    font-size:14px;
    line-height:15px;
    min-height:14px;
    margin-top:4px;
    margin-bottom:0;
    height:inherit;
}

.content-item .content-meta,
.content-item.topic-item .content-meta  {
    font-size:12px;
    line-height:13px;
    min-height:12px;
}

.content-item.write-tutorial {
    position: relative;
    left: calc(-200% + 20px);
    min-width: calc(200% - 32px);
    margin-top:-74px;
    height: 60px;
}

#content > div > div:nth-child(2) > section:nth-child(1),
#content > div > div:nth-child(3) > section:nth-child(2) {
    -webkit-box-flex: 2;
    -ms-flex: 2;
    flex: 2;
}

.content-item .content-authors {
	display:none !important;   
}

.content-item .content-meta {
    margin-top:2px;
}

.content-item {
	margin:0 5px 10px;   
	flex: 1 1 300px;
}

.content-item.has-reactions .content-meta {
    padding-left:0px !important;
}

.content-item .content-reactions {
	transform:scale(0.8);
    left:-24px;
    bottom:-2px;
}

.btn.btn-grey {
	padding:0 10px;   
}
}
