css:
@-moz-document domain("kisscartoon.es")  {

/* i'm pickle Riiick */

#head .left_head {filter: hue-rotate(180deg) brightness(.7) !important; }  
  
[style="visibility:visible;width:336px;height:768px;position:absolute;left:50%;margin-left:-821px;top:0;z-index:0;"] {
display: none !important;
}
[style="visibility:visible;width:336px;height:768px;position:absolute;left:50%;margin-left:485px;top:0;z-index:0;"]  {
display: none !important;
}
.arrow-general, .wrap, [style="position: static !important;"] *, [src*='promo'], [id*='ScriptRoot'] {
display: none !important;
}
  
* {
border: none !important;
}  
  
#container, #leftside {
width: 80% !important;
}  
  
#subcontent {
width: -moz-fit-content !important;
}

#navsubbar, .barTitle, #navcontainer li a#currentTab {
background: none !important;
background-color: rgba(100,100,100, .5) !important;
}
  
.tabactive {
color: #CA640B !important;
}  
  
#tabmenucontainer a:link, #navcontainer a:link, #imgSearch, #btnReloadRandom {
background: none !important;
background-color: rgba(70,70,70, .5) !important;
}
    
.details, #navbar {
background: none !important;
background-color: rgba(20,20,20, .5) !important;
}  
  
.title, a, .bigChar {
color: rgb(250,250,250);
}
  
.slide_items .owl-theme .owl-controls .owl-buttons div {
top: -32px;
transform: scale(1.06);
}  
  
.item_film_list * :not(.title){
color: grey !important;
}

}
