scrollbar {
    -moz-appearance: none !important;
    border: none !important}

scrollbar {
    /*background: #2f3029 !important}*/
    background: #242424 !important}

scrollbar thumb {
    -moz-appearance: none !important;
    border: 1px solid #454545 !important;
    background: #454545 !important;
    /* background: #707070 !important;*/
    background-clip: padding-box !important;
    -moz-transition: ease-in .2s !important}


scrollbar thumb:not(:active):hover {
   /* border-color: #e6db74 !important;
    background-color: #e6db74 !important;*/
    
    border-color: #747474 !important;
    background-color: #747474 !important;
    background-clip: padding-box !important}


scrollbar thumb:hover:active,
scrollbar thumb:active,
scrollbar thumb:hover[active="true"] {
    /*border-color: #cb8b32 !important;
    background: #cb8b32 !important;*/
    border-color: #A2A2A2 !important;
    background: #A2A2A2 !important;
    background-clip: padding-box !important}


scrollbar scrollbarbutton, 
scrollbar gripper, 
scrollcorner {
   /* -moz-appearance: none ! important;*/
    filter: invert(100%);
    
    /*border: 1px solid #272822 !important;
    background: #272822 !important;
    width: 10px;*/
    -moz-transition: ease-in .2s !important}
     








