css:
@-moz-document url(chrome://browser/content/browser.xul) {
  #ublock0-button label.toolbarbutton-badge {
    background: none !important;
    /*border-radius: 20px !important;*/
    border: 0px !important;
    font-size: 9px !important;
    
    padding: 0px 3px !important; } }
  /* ↑ if you don't want the badge edit, delete ALL this up here ↑ */

@-moz-document url("chrome://ublock0/content/popup.html") {
  
  body {
    background-color: #383838 !important; 
    color: #aaaaaa;
    font-family: Droid Sans, Segoe UI, Trebuchet MS, Tahoma, sans;
  }
  
  h2, p {
    color: #aaa !important;
    background-color: #383838 !important; 
    margin-top: 2px !important;
    margin-bottom: 2px !important;
  }
  
  #extraTools {
    margin-top: 2px !important;
    background-color: #383838 !important; 
  }
  
  #extraTools > span:hover {
    color: #ffffff; 
    transition-duration: 0.2s;
  }
  
  #gotoPrefs {
    background: transparent !important;
  }
  
  body:not(.off) #switch .fa {
    color: #ff3333 !important;
  }
  
  h2 {
    font-weight: bold !important;
  }
  /*
  #switch,
  #switch > *,
  #refresh,
  #refresh > * {
    font-size: 32px !important;
    line-height: 36px !important;
    padding-top: 0 !important;
    padding-bottom: 0 !important;
    margin-top: 0 !important;
    margin-bottom: 0 !important;
  }
  
  
  #refresh {
    margin-top: 2px !important;
    color: rgb(191, 0, 0) !important;
    background: white !important;
    padding-top: 0 !important;
    padding-bottom: 0 !important;
    border: none !important;
    position: absolute !important;
    top: 0 !important;
  }*/
   #switch span.fa {
    font-size: 35px !important;
  }
  #switch .fa {
    color: #ff360c;
  }
  #refresh {
    background-color: #cf4747 !important;
    border: 0px !important;
    border-radius: 0px !important;
    color: #EAEAEA;
    display: none;
    font-size: 24px !important;
    left: 0px !important;
    right: 0px !important;
    line-height: 1.10 !important;
    padding: 8px 8px !important;
    position: absolute !important;
    top: 0px !important;
  }
  #refresh:hover {
    /*transform: rotate(30deg);*/
    transition-duration: 0.2s;
    color: #484848 !important;
  }
    #gotoPrefs #version {
    font-size: 0px !important;
  }
    #gotoPrefs {
    /*
    position: absolute !important;
    bottom: 0px !important;
    font-size: 8px !important;
    
    text-transform: uppercase !important;*/
    width: 100% !important;
    background-color:#383838!important;
    /*margin-bottom: -3px !important;*/
    color:white !important;
  }

  #tooltip {
    background-color: #383838;
    border: 0px !important;
    border-radius: 2px;
    box-shadow: 1px 1px 4px gray;
    color: #fafafa;
    font: 11px sans-serif;
  }
}
