css:
@-moz-document domain("netrunner.cc") {
body {
    background: #140922;
}

div#content {
    margin: auto;
    padding: 2em;
    max-width: 960px;
}

div#headBar {
    background: #3d1daf;
    color: #faf7cd;
    margin: 0;
    height: 80px;
}

.boxHeader {
    color: #faf7cd;
    background-color: #3d1daf;
}

.boxContent {
    background-color: #1b0738;
}

.irc {
    color: #ffce61;
}

a:link {
    color: #ec2380;
}

a:visited {
    color: #ec2380;
}

div#content {
    color: #33d9d0;
}

img.logoImg {
    display:none;
}

.logo {
	margin-left: calc(50% - 136.484px);
    height: 100%;
}

h2 {
    display: block;
    font-size: 2em;
    font-weight: bold;
    margin: 0;
	margin-top: 16px;
}
}
