css:
@-moz-document domain("weibo.com") {
body.FRAME_login {
    background: #fc4a1a!important;
    /* fallback for old browsers */
    
    background: -webkit-linear-gradient(to right, #f7b733, #fc4a1a)!important;
    /* Chrome 10-25, Safari 5.1-6 */
    
    background: linear-gradient(to right, #f7b733, #fc4a1a)!important;
    /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */
}
.W_unlogin_v4 .login_box {
    border-radius: 20px;
    padding: 0 20px 6px;
}
.FRAME_login .WB_frame #plc_main {
    padding-left: 0!important;
    background: transparent!important;
    width: 300px!important;
}

.FRAME_login .WB_frame #plc_main:before {
    background: none;
}

.UG_box {
    background-color: transparent!important;
    padding: 0!important;
}
#pl_unlogin_home_hots {
    display: none;
}
.FRAME_login .WB_frame {
    top: 45%;
    left: 50%;
    transform: translate(-50%, -50%);
    position: fixed;
    margin: 0!important;
    display: flex;
    justify-content: center;

}

.FRAME_login #plc_main .WB_main_r{
	margin-left: 0!important;
}
.W_unlogin_v4 .W_login_form .register,
.info_list.other_login {
    display: none!important;
}
.info_list.auto_login {
    margin-top: 30px
}
.info_list.login_btn {
    margin-top: 30px;
}
.W_unlogin_v4 {
    margin: 0 auto;
}
.W_btn_a {
    box-shadow: 0 1px 5px 1px #f99c6d!important;
}
.W_unlogin_v4 .info_header .qrcode_target{
    right: -20px;
}
}
