css:
@-moz-document domain("bestblackhatforum.com") {
body {
    background: #000000;
    color: #ccc;
    font-family: 'Roboto', sans-serif;
    font-size: 13px;
    text-align: center;
    line-height: 1.4;
    padding: 0px;
    margin: 0px;
}
#container {
    border: none
}
a:link{
	-moz-transition: all 0.5s ease;
    -webkit-transition: all 0.5s ease;
    -o-transition: all 0.5s ease;
    -ms-transition: all 0.5s ease;
    transition: all 0.5s ease;
}
a:link:hover{
	text-decoration: none;
}
.navbar ul#list-nav {
    background: #000;
    border: none;
    height: 36px;
    list-style: none outside none;
    margin: 0;
    padding: 0;
    padding-left: 6px;
    z-index: 1;
    border-radius: 0;
}
.navbar ul#list-nav li a {
    border-right: 1px solid #000000;
    color: #FFCC00;
    float: left;
    line-height: 33px;
    padding: 0 5px;
    text-align: center;
    text-decoration: none;
    width: 100px;
}
#panel {
    background: #1D1D1D;
    border: none;
    color: #ccc;
    font-size: 13px;
    border-radius: 0px 0px 5px 5px;
    padding: 15px;
    text-shadow: none;
}
.thead {
    background: #000;
    border-radius: 0;
    color: #ffffff;
    height: 36px;
    padding: 6px 8px;
    text-shadow: -1px -1px 0px #000;
}
.tborder {
    background: #000;
    width: 100%;
    margin: auto auto;
    border: none !important;
    border-radius: 0;
    padding: 1px;
    text-shadow: none;
    font-family: 'Roboto', sans-serif;
    font-size: 16px;
}
.trow1 {
    background: #1D1D1D;
    font-size: 14px;
    font-weight: lighter;
}
.trow2 {
    background: #131212;
    font-size: 14px;
    font-weight: lighter;
}
input.button {
    background: #FFCC00;
    border-radius: 5px;
    color: #1d1d1d;
    padding: 8px 13px;
    text-transform: uppercase;
    font-size: 13px;
    text-shadow: none;
    opacity: 1;
    border: none;
    cursor: pointer;
    font-weight: bold;
    -moz-transition: all 0.5s ease;
    -webkit-transition: all 0.5s ease;
    -o-transition: all 0.5s ease;
    -ms-transition: all 0.5s ease;
    transition: all 0.5s ease;
}
input[name="keywords"] {
    height: 30px;
    border: 1px solid;
    border-radius: 4px;
    padding-left: 5px;
    font-size: 14px;
}
input[name="subject"] {
    background: #fff;
    border: 1px solid #000;
    border-radius: 5px;
    padding: 10px !important;
    color: #3d3d3d !important;
    height: 20px !important;
    font-size: 14px;
}
.thead a:link {
    color: #ffffff;
    text-decoration: none;
    font-size: 15px;
    text-align: center;
    text-shadow: none;
}
.thead h1 {
    height: 20px
}
.thead h1 a {
    font-size: 20px !important;
}
.tcat {
    background: #0D0D0D;
    color: #ccc;
    font-size: 12px;
    height: 30px;
}
a.postbit {
    background: #000;
    color: #fff;
    text-shadow: 0px -1px 0px #000;
    border: 1px solid #000;
    border-radius: 3px 3px 3px 3px;
    font-size: 11px;
    padding: 4px 7px 4px;
    text-transform: uppercase;
    opacity: 1;
    -moz-transition: all 0.5s ease;
    -webkit-transition: all 0.5s ease;
    -o-transition: all 0.5s ease;
    -ms-transition: all 0.5s ease;
    transition: all 0.5s ease;
}
.tfoot {
    background: #1d1d1d;
    color: #ffffff;
    padding-bottom: 10px;
}
.pagination {
    font-size: 13px;
    padding-top: 10px;
    margin-bottom: 5px;
}
.pagination .pagination_current {
    background: #FFCC00;
    font-weight: bold;
    color: #000;
    text-shadow: none;
    font-size: 13px;
    border: 1px solid #FFCC00;
}
.pagination a {
    border: 1px solid #FFCC00;
    -moz-transition: all 0.5s ease;
    -webkit-transition: all 0.5s ease;
    -o-transition: all 0.5s ease;
    -ms-transition: all 0.5s ease;
    transition: all 0.5s ease;
}
.pagination a:hover {
    background: #FFCC00;
    text-shadow: none;
    color: #000;
}
a.postbit_large {
    background: #FFCC00;
    border-radius: 5px;
    color: #1d1d1d;
    margin-top: 10px;
    padding: 8px 13px;
    text-transform: uppercase;
    font-size: 13px;
    text-shadow: none;
    opacity: 1;
    border: none;
    cursor: pointer;
    font-weight: bold;
    -moz-transition: all 0.5s ease;
    -webkit-transition: all 0.5s ease;
    -o-transition: all 0.5s ease;
    -ms-transition: all 0.5s ease;
    transition: all 0.5s ease;
}
input.textbox {
    background: #fff;
    border: 1px solid #000;
    border-radius: 5px;
    padding: 3px;
    color: #3d3d3d;
}
.messageEditor .toolbar_dropdown .editor_dropdown_label {
    min-width: 120px;
    background: #fff;
    padding: 3px 4px;
    margin-right: 14px;
    border: 1px solid #aaa;
    border-right: 0;
    height: 14px;
    overflow: hidden;
    color: #000;
}
.messageEditor .editor_dropdown_menu li {
    list-style: none;
    padding: 3px 3px 3px 12px;
    margin: 0;
    position: relative;
    cursor: pointer;
    color: #000;
}
}
