css:
@-moz-document url-prefix("http://orteil.dashnet.org/cookieclicker/"), url-prefix("https://orteil.dashnet.org/cookieclicker/") {
.product { height: 24px; }

.title[id="productName0"],
.title[id="productName1"],
.title[id="productName2"],
.title[id="productName3"],
.title[id="productName4"],
.title[id="productName5"],
.title[id="productName6"],
.title[id="productName7"],
.title[id="productName8"],
.title[id="productName9"],
.title[id="productName10"],
.title[id="productName11"],
.title[id="productName12"],
.title[id="productName13"],
.title[id="productName14"],
.title[id="productName15"],
.title[id="productName16"] { display: none; }

