css:
@-moz-document domain("www.jeuxvideo.com") {
/*24 heures*/

#chart-2 > div > div {
	background-color:#132d2f !important;
    border-style:none !important;
}

/* Titre */

#chart-2 > div > div > div.header-div {
    border-color:#90adaf !important;
}

#chart-2 > div > div > div.header-div {
    background-color:#132d2f !important;
}
/*Nom de la crypto*/
#chart-2 > div > div > div.header-div > a:nth-child(1) {
    color:#b5b5b0 !important;
}

/* Intervalle*/

#chart-2 > div > div > div.header-div > a:nth-child(2) {
    color:#b5b5b0 !important;
    background-color:#132d2f !important;
    border-style:none !important;
}

/*Prix actuel*/

#chart-2 > div > div > div:nth-child(2) {
    background-color:#132d2f !important;
}

#chart-2 > div > div > div:nth-child(2) > a > span:nth-child(1) {
    color:#b5b5b0 !important;
}

/*Last updated et marketcap*/

#chart-2 > div > div > div:nth-child(3) {
    background-color:#132d2f !important;
}

#chart-2 > div > div > div:nth-child(3) > * {
    color:#b5b5b0 !important;
}

#chart-2 > div > div > div:nth-child(4) > * {
    color:#b5b5b0 !important;
}

/*Courbe*/
#chart-2 > div > div > div:nth-child(6) > canvas{
    background-color:#132d2f;
}

/* 1 semaine*/

#chart-1 > div > div {
	background-color:#132d2f !important;
