css:
@-moz-document domain('wf.xuerian.net') {
    body {
        background-color: #202020;
        background-image: none;
        color: #eee;
    }
    a {
        color: #3150ff;
    }
    #nav > div {
        background-color: #2b2b2b
    }
    .wishlist .list > li, .reliquary .relic, .reliquary .details, .reliquary .source {
        background-image: inherit;
        background-color: #2b2b2b;
    }
    .wishlist .list > li:nth-child(odd), .reliquary .relic:nth-child(odd), .reliquary .details:nth-child(odd), .reliquary .source:nth-child(odd) {
        background-color: #222
    }
    .wishlist .list .set:hover {
        box-shadow: 0px 0px 15px 0px #bbdefb;
        border: 2px solid #2196f3;
        border-image: none;
    }
    .wishlist .list .set.vaulted > div {
        color: #d80000;
    }
    #tokeninfo {
        opacity: 1;
    }
    .reliquary .relic .details, .reliquary .source .details {
        background-color: inherit;
    }
    .fissures, .node.active-fissure {
        color: #222;
    }
    .wishlist .list .item.wanted {
        background-image: linear-gradient(to right, rgba(25, 150, 0, 0.4) 10px, rgba(25, 150, 0, 0.1) 25px, transparent 95%);
    }
}
