css:
@-moz-document url-prefix(https://regexcrossword.com) {
    body {
        background: black;
        color: white;
    }

    .puzzle.puzzle-grid table tfoot .highlight,.puzzle.puzzle-grid table thead .highlight,
    .puzzle.puzzle-grid table tbody .highlight input:focus,.puzzle.puzzle-grid table tbody .highlight th,
    .puzzle.puzzle-hexagonal.editmode .clue input,
    .puzzle.puzzle-hexagonal .grid .row .clue.highlight,
    .puzzle.puzzle-hexagonal .grid .hex.highlight {
        background: #69838c !important;
    }

    .puzzle.puzzle-hexagonal .grid .hex.highlight:after,.puzzle.puzzle-hexagonal .grid .hex.highlight:before {
        border-bottom-color: #ffc;
        border-top-color: #ffc
    }

    .jumbotron {
        filter: invert(1.0);
        color: black;
    }

    .jumbotron .btn-info {
        color: black;
        background-color: #A43F21;
        border-color: #B94725;
    }

    .table-striped>tbody>tr:nth-of-type(odd) {
        background-color: #333;
    }

    .puzzle.puzzle-grid table tbody td.space {
        background: white;
    }
}
