css:
@-moz-document url-prefix("http://www.goatlings.com/") {
.search-mid {
color: #f8f8c4;
}

#sidebar input[type=submit] {
    border: 4px solid #bc9c71;
    border-radius: 5px;
    background: #a17a50;
    padding: 2px 15px;
    color: #f8f8c4;
    font-weight: bold;
}

#sidebar input[type=submit]:hover {
    background: #865f37;
     -moz-transition:background .2s ease-in;
    -o-transition:background .2s ease-in;
    -webkit-transition:background .2s ease-in;
    border: 4px solid #9e774f;
    -moz-transition:border .1s ease-in;
    -o-transition:border .1s ease-in;
    -webkit-transition:border .1s ease-in;
    cursor: pointer
}
#sidebar input[type=text] {
       background: #be966b;
    color: #4A2A1F;
    border: 2px solid #d7b88e;
        border-radius: 3px;
}

#sidebar select[name=specifics] {
       background: #be966b;
    color: #4A2A1F;
    border: 2px solid #d7b88e;
        border-radius: 3px;
}

#sidebar select[name=type] {
    color: #4A2A1F;
       background: #be966b;
    border: 2px solid #d7b88e;
        border-radius: 3px;
}

}
