css:
@-moz-document
domain(www.devrant.io),
domain(devrant.io),
domain(www.devrant.com),
domain(devrant.com) {
.body-col1 {
    margin-left: -380px;
}

.body-col2, .rantlist-bg {
    width: 100%;
}

.rant-image {
    max-height: 550px;
}

.rantlist-content-col {
    width: calc(100% - 55px);
    min-height: 52px;
    padding-bottom: 34px;
}

.rantlist-title-text {
    width: 90%;
}

.addrant-btn {
    right: 25px;
    left: auto;
}

.rantlist-content {
    width: auto;
}

.reply-row .rantlist-content-col {
    width: calc(100% - 83px);
}

.rantlist-title, .reply-row .rantlist-title {
    width: 80%;
}

.reply-row .rantlist-content-col {
    padding-bottom: 0;
}

.addcomment-btn {
    left: auto;
    right: 25px;
}

.rantlist-tags {
    position: absolute;
    bottom: 0;
    max-width: none;
}

.rantlist-comments {
    position: absolute;
    bottom: 0;
    right: 0;
}
}
