css:
@-moz-document regexp("https?://www\\.tremorgames\\.com/(index.php)?\\?.*page=([^1]|\\d{2,})") {
.pagination1>a:nth-of-type(2){
	position:fixed;
	bottom:5px;
	left:5px
}
.pagination1>a:nth-of-type(2){
	padding:20px;
	font-size:40px
}
}

@-moz-document regexp("https?://www\\.tremorgames\\.com/(index.php)?\\?.*action=shop.*") {
.pagination1>.currentpage~a:nth-last-of-type(3){
	position:fixed;
	bottom:0;
	right:0
}
.pagination1>.currentpage~a:nth-last-of-type(3){
	padding:20px;
	font-size:40px
}
}
