css:
@-moz-document regexp("https?://github.com/.+?/.+?/pull/[0-9]+/files.*"), regexp("https?://github.com/.+?/.+?/blob/.*") {
.container {
    width: 90%;
}

.container-lg {
    max-width: 90%;
}
}

@-moz-document domain("github.com") {
.container {
    transition: width 1s ease;
}

.container-lg {
    transition: max-width 1s ease;
}
}
