css:
@-moz-document domain("www.senscritique.com") {
#header-autocomplete li:hover
{
	color: white;
}

.body-habillage
{
	background-color: #EFEFEF;
}

.d-grid-extend .d-grid-aside
{
	background-color: #EFEFEF;
	padding: 5px;
}

.d-grid.d-bodyWrapper
{
	padding-top: 0;
}

.d-grid.ecbi-billboard-popular
{
	bottom: 5px;
	left: calc(50% + 53px);
	margin-left: -522px;
	width: calc(1040px - 53px - 53px);
}

.d-rubric
{
	margin-bottom: 30px;
}

.d-rubric-border
{
	padding-left: 0;
}

.d-rubric.laho-wrapper.laho-wrapper-feed
{
	margin-top: 20px;
}

.d-tabs.d-grid
{
	background: #DDD;
	width: 100%;
}

.ecbi-billboard > li
{
	height: 155px;
}

.ecbi-billboard-content > *
{
	visibility: hidden;
}

.ecbi-billboard-nav
{
	bottom: 65px;
}

.ecbi-billboard-navAction
{
	bottom: auto;
}

.eins-search-header,
.header-logo-small.eins-logo-small,
.header-social-item
{
	filter: invert(100%);
}

.header-content-wrapper,
header.header,
header.header.sticky,
header.header::before
{
	background-color: #161822;
	color: white;
}

.header-navigation-main-list.no-universe
{
	overflow: hidden;
}

.header-placeholder
{
	background: #6C6E78;
	height: 0;
}

.header-user-name
{
	color: white;
}

.header-user-ratings
{
	background-color: #363842;
}

.rvi-review,
.elfs-story
{
	background-color: #FAFAFA;
}

.wrap > .d-bodyWrapper.d-grid > .laho-wrapper.d-rubric > .hoin-main.laho-main > div:nth-of-type(2) > .laho-contentGap > .elfs-story:nth-of-type(1) > .elfs-story-innercontent > .elfa-attachment
{
	display: none;
	visibility: hidden;
}

/*body
{
	position: relative;
	top: -220px;
}*/

div.wrap
{
	padding-top: 137px;
}

header.header,
header.header.sticky
{
	-moz-transition: opacity 1.0s ease-out;
	-o-transition: opacity 1.0s ease-out;
	-webkit-transition: opacity 1.0s ease-out;
	opacity: 0.5;
	transition: opacity 1.0s ease-out;
}

header.header:hover,
header.header:focus,
header.header:active,
header.header.sticky:hover,
header.header.sticky:focus,
header.header.sticky:active
{
	-moz-transition: opacity 0.3s ease-out;
	-o-transition: opacity 0.3s ease-out;
	-webkit-transition: opacity 0.3s ease-out;
	opacity: 0.90;
	transition: opacity 0.3s ease-out;
}

img.lazy.pvi-poll-figure
{
	width: 290px;
}

ul.header-navigation-universe-others-list,
#header-autocomplete li
{
	color: black;
}

hear.header
{
	position: fixed;
	top: 0;
	z-index: 90;
	animation-name: headerStickyEntry;
	animation-duration: .6s;
	left: 50%;
	transform: translateX(-50%);
	width: inherit;
}

a:active>div>div>span,
a:focus>div>div>span,
a:hover>div>div>span,
a:active>div:last-child,
a:focus>div:last-child,
a:hover>div:last-child
{
	/*color: #030303;*/
	
	color: inherit;
}

a:hover>div.elrua-useraction>div.elrua-useraction-action,
a.elrua-useraction:hover>div.elrua-useraction-action
{
	background-color: #dbdbdd;
}
}

@-moz-document regexp("^(.*)/feedpad/(.*)$") {
body
{
	top: auto;
}

div[data-reactroot] > div:nth-of-type(1)
{
	-moz-transition: opacity 1.0s ease-out;
	-o-transition: opacity 1.0s ease-out;
	-webkit-transition: opacity 1.0s ease-out;
	opacity: 0.5;
	transition: opacity 1.0s ease-out;
}

div[data-reactroot] > div:nth-of-type(1):hover,
div[data-reactroot] > div:nth-of-type(1):focus,
div[data-reactroot] > div:nth-of-type(1):active
{
	-moz-transition: opacity 0.3s ease-out;
	-o-transition: opacity 0.3s ease-out;
	-webkit-transition: opacity 0.3s ease-out;
	opacity: 0.90;
	transition: opacity 0.3s ease-out;
}
}
