css:
@-moz-document domain("appliedlogic.atlassian.net") {
.ghx-issue:hover{
    width: 400px;
    height: 180px;
    z-index: 100;
}
.ghx-issue:active{
    width: inherit;
}

.ghx-issue:hover .ghx-avatar-img{
    border: 2px solid;
    width: 110px;
    height: 110px;
    position: absolute;
    bottom: 0;
    right: 0;
}

.ghx-issue {
    color: inherit;
    height: 180px;
}

#ghx-column-header-group.ghx-fixed{
    z-index: 9999;
}

.ghx-summary:after{
    content: attr(title);
}


}
