/* Notice I have not added themeing for the user areas yet */

/* ---Body--- */
body {
	color: #fff;
	background-color: #0e0e0e;
}

/* ---Landing page notice--- */
.region-highlighted {
	background-color: #336299;
}

/* ---Page--- */
#page {
	background-color: #2b2b2b;
	color: white;
}

/* ---Page titles--- */
.form-item label {
	font-size: 1em;
	color: #fff;
}

/* ---Left side bar--- */
#sidebar-left .block, #sidebar-right .block {
	background-color: #2b2b2b;
}
}
