css:
@-moz-document domain("pinterest.com") {
/* Darker */

.secretBoardsWrapper,
.BrioProfileHeaderWrapper .tabBar .tabItem.active {
    background: #353945 !important;
}
/* Medium */

body,
.reactCloseupScrollContainer > div,
div.closeupContainer,
.ExploreStoryCredits,
.flex > div:nth-child(2) > div > div > div > button > div,
.flex > div:nth-child(1) > div > button > div {
    background: #404552 !important;
}
._5f,
.BrioProfileHeaderWrapper .fixedHeader,
.ReactBoardInfoBar {
    background: rgba(64, 69, 82, 0.75)
}
/* Text color */

._5k,
.SearchFormInReact .searchInput,
.Pin.summary .pinDescription,
.Button.btn.isBrioFlat.primary {
    color: rgba(211, 218, 227, 0.75) !important
}
.creditName,
.creditTitle,
.dark-gray,
.tabText {
    color: rgba(211, 218, 227, 0.4) !important
}
._0._4v._3z._2._48._h._jp > div > button > svg > path {
    fill: rgba(211, 218, 227, 0.75)
}
._ts {
    border: none !important
}
/* Blue */

._0._4v._3z._2._i > div > div > div:nth-child(1) > div > a > div > div path,
._mn._3m._3s._mp._56 path,
._0._4v._3z._54._5f > svg > path {
    fill: #4d80be !important
}
.Button .buttonBadge.leftBadge,
.red,
.bg-red,
.standardForm .formFieldMessage,
.Button.btn.isBrioFlat.primary,
.Button.btn.isBrioFlat.primary:active,
._5d,
.buttonInoutWrapper button {
    background: #4d80be !important
}
.bg-white,
._0._4v._3z._ll button,
.bg-super-light-gray,
._0._4v._3z._2._48._4r._h > div > button > div,
._5h {
    background: transparent !important
}
.privacyPolicyWrapper,
._0._4v._3z._ll > div:nth-child(3) {
    display: none
}
._2k {
    width: 15%
}
.BrioProfileHeaderWrapper .editSettingsButton svg {
    height: 18px;
    width: 18px;
}
._5f ._6l {
    padding-top: 0;
    padding-bottom: 0
}
.gridCentered {
    padding: 10px !important
}
.ReactBoardInfoBar.fixedHeader,
.BrioProfileHeaderWrapper .fixedHeader {
    top: 56px !important
}
}
