css:
@-moz-document domain("www.mixcloud.com") {
body {
    background: #212121;
}
.contents-inner-wrapper {
    background: #424242;
}
.header:before {
    background: #111111;
}
.player,
.player-wrapper:before {
    background: #111111;
    box-shadow: none;
}
.header .search input {
    background: #424242;
}
.site-wrapper {
    box-shadow: none;
    /* the weird diagonal lines were a background */
    background: transparent;
}
.card-author .card-author-action a,
.card-title h1 a,
.people-to-follow .person-to-follow b a{
    color: #dadada;
}
.bottom-border, h1 {
    color: #cdd9e4;
    border-bottom: 2px solid #676767;
}
.card {
    border-bottom: 1px solid #676767;
}
.tag .chart-position {
    background: rgb(103, 103, 103);
}
.tag {
    color: #bababa;
    border: 1px solid #676767;
}
.play-button .play-button-rings-svg circle.ring-listened {
    stroke: #4fa6d3 !important;
}
.play-button .play-button-rings-svg circle.ring-remaining {
    stroke: #676c6e;
}
.play-button .state {
    color: #cdd9e4;
}
.people-to-follow li {
    border-bottom: 1px solid #676767;
}
.bottom-border b {
    color: #dadada;
}
}
