css:
@-moz-document domain("fi.nobelium.xyz") {
.navbar-brand img {
	content: url("https://fi.nobelium.xyz/img/logo2.fw.png");
    width: 50px;
    height: 50px;
    vertical-align: middle;
}

.navbar-brand {
	font-size: 0;
    padding-top: 1px;
    padding-bottom: 1px;
}

.navbar-light .navbar-nav .nav-link:hover {
	color: rgb(53, 53, 53)!important;
}

.container.main-content.text-center img {
    content: url("https://fi.nobelium.xyz/img/logo2.fw.png")
}

.navbar-light .navbar-nav .nav-link {
	color: rgb(153, 153, 153)!important;
}

.nav-item.nav-link.n-money-text::before {
	content: "\f0d6";
}

.nav-item.nav-link.n-money-text {
	font: normal normal normal 14px/1 FontAwesome, system-ui;
    padding-top: 10px;
}

.nav-item.nav-link.n-money-text img {
	display: none;
}

.navbar-light.bg-faded {
	box-shadow: 0 1px 2px 0 rgba(34,36,38,.15);
    font-family: Source Sans Pro;
    font-weight: 500;
    padding-top: 0px;
    padding-bottom: 0px;
}

.alert {
	display: none;
}
}
