css:
@-moz-document domain("www.deyi.com") {
#postlist >div>table>tbody>tr:nth-child(3) {
  display: none;
}
#postlist >div>table>tbody>tr>td.plc {
  width: 100%;
}
#postlist td.pls>*:not(.pi) {
  display: none;
}
#postlist td.pls>.pi {
  border: none;
}
#postlist .t_fsz {
  min-height: initial;
}
#postlist .plc {
  position: relative;
}
#postlist .plc>.pi {
  position: absolute;
  top: 0;
  right: 0;
  width: 50%;
  opacity: 0;
  pointer-event: none;
}
#postlist .plc:hover>.pi {
  opacity: 1;
  pointer-event: initial;
}
#postlist .pcb>*:not(:first-child) {
  display: none;
}
#postlist .pct {
  padding: 0.5em 0;
}
#postlist .plc.plm {
  display: none;
}
}
