css:
@-moz-document domain("businessdesk.faithlife.com") {
BODY,
.data-table th,
.header,
.tableheader,
.gridHeader,
.rolesBackground,
.listBackground,
.listSolidBackground,
.yui-ac-results-container li.yui-ac-highlight,
.searchResults .ui-autocomplete-even,
.multiSelectOptions > LABEL:hover,
div.navmenu ul.dynamic,
table.userRoles {
    background-color: #DDD;
}
.card,
.data-table,
.modalPopup,
.modal-tooltip,
.filter-container {
    background-color: #EEE;
}
.data-table thead > tr {
    border: 2px solid #EEE;
}
.ui-widget-header,
.ui-state-default,
.ui-widget-content .ui-state-default,
.ui-widget-header .ui-state-default,
.ui-button,
html .ui-button.ui-state-disabled:hover,
html .ui-button.ui-state-disabled:active {
    background-color: transparent;
}
}
