body {
    text-shadow: 1px 1px 5px black;
    background-color: #424242;
}

h1, h2, h3, h4 {
    color: white;
}
div.dashboard-jumbotron{
    background: #313030;
}

.animated{
    background-color: #032e3c;
}

.img-circle {
background: #006687;
}

a {
-moz-transition:all 1s;
-o-transition:all 1s;
transition:all 1s;
}

a:hover {
    background-color: #008cba;
    text-shadow: 0 0 0;
}
}
