css:
@-moz-document domain('www.tenkarstavern.com') {
  
  html div.column-center-outer {
  background: #ECEFF1 none;
}
html body div>div>div>div#main {
  font: 15px Roboto, sans-serif;
  color: rgba(0, 0, 0, 0.87);
}
html body div>div>div>div#main a {
  color: #0D47A1;
}

html .comments .comment-thread.inline-thread { 
  background: #fff;
}

html #comments .avatar-image-container img {
  border-width: 0;
  border-radius: 4px;
}

html h2.date-header { color: #0D47A1;}
html div.post-footer {
  background: #CFD8DC none;
  border-radius: 4px;
  border-width: 0;
  box-shadow: 0 2px 2px rgba(0,0,0,0.57);
  color: rgba(0, 0, 0, 0.87);
}
  
}
