css:
@-moz-document domain("blackboard.secure.force.com") {
body{
    background-color:#000;
    color: #ccc;
}
#menu .nav li a {
    color: #def;
    font-size: 13px;
    font-weight: 400;
    padding: 10px 20px;
}
#menu .nav li.active > a, #menu .nav li.open > a, #menu .nav li:hover > a {
    background-color: #00315c;
}
.dropdown-menu{
    background-color: #000;
}
#menu .nav ul.dropdown-menu a:hover {
    background-color: darkblue;
}
.right-link a {
    color: lightblue;
}
.btn-primary {
    color: white;
    background-color: #00315c;
    border-color: #00315c;
}
table[role="presentation"]{
	background-color: transparent !important;
    color: #fff;
}

}
