css:
@-moz-document domain("www.signaly.cz") {
.site {
	background: #1f1f1f
}
.post-create {
	background: #545454
}
.section-stream .post {
	background: #545454
}
.section-stream .section-comments {
	background: #545454
}
.section-comments .story .story-content {
	color: #1f1f1f
}
body {
	color: #fff
}
a {
	color: #0092ff
}
.section-stream .comments-toolbar {
	background: #4c9945
}
.comments-toolbar a {
	color: black
}
.panel,
.panel-content,
.panel-content-sm {
	background: #545454
}
.alert {
	background: black
}
.groups-sections .section-em {
    background: #545454
}		
.section-footer {
    background: #000
}
.chat-window-header {
    background: #55ab4e;
    color: #000
}
.chat-window {
    background: #545454;
}
.comments-toolbar .ficon {
    color: black;
}
.section-stream .content :last-child {
    color: #0092ff;
}
.section-usermenu>.username .dropdown-toggle {
    color: black
}
.chat-message:not(.chat-message-actor) .chat-message-fragments {
    color: black;
    background: #6f6f6f
}
.chat-message.chat-message-actor .chat-message-fragments {
    color: black;
    background: #b3b3b3
}
.chat-message-form textarea.input {
    color: black;
}
.chat-roster .input {
    color: black;
}
}
