css:
@-moz-document domain("www.schemers.org") {
/***********************
GENERAL
***********************/
body {
    background-color: white;
    font-family: Verdana, Geneva, sans-serif;
}

h1, h2, h3 {
    font-family: "Arial Black", Gadget, sans-serif;
}

tt {
    font-family: "Courier New", Courier, monospace;
    background: ghostwhite;
}

.navigation {
    color: darkslategray;
    font-size: .9em;
    font-style: normal;
    font-weight: bold;
    font-family: "Courier New", Courier, monospace;
    background: AliceBlue;
    border: 1px dotted CornflowerBlue;
    border-radius: 6px;
    padding: 5px;
}

.navigation a {
    text-decoration: underline;
}

/***********************
LINKS
***********************/
a {
    text-decoration: none;
}

a:link {
    color: green;
}

a:visited {
    color: seagreen;
}

a:hover {
    background-color: moccasin;
}

a:active {
    background-color: gold;
}


/***********************
SIDE NAV BAR
***********************/
ol, li {
    font-size: 14px;
}

ol ol, li li {
    font-size: 12px;
    font-style: italic;
}

ol ol ol, li li li {
    font-size: 10px;
    font-style: italic;
}
}
