css:
@-moz-document url-prefix("http://www.irodemine.com/divinity2/") {
body {
    background-color: #e8f8ff;
    font-family: 'Open sans', sans-serif;
    color: #333;
}
form {
    padding-top: 30px;
    padding-bottom: 15px;
}
p {
    padding: 10px 0;
    margin: 0;
}

a:link {
    color: #333;
    text-decoration: none;
}

a:visited {
    color: #666;
    text-decoration: none;
}

a:hover {
    color: #28b;
}

a:active {
    color: #2ce;
}
table {
    border-collapse: collapse;
    background-color: #fff;
    -moz-border-radius: 0px;
    border-radius: 0px;
    ;
}
tr:hover {
    background-color: #f8f8f8;
}
form > table > tbody > tr:hover {
    background-color: inherit;
}
form > table {
    background-color: rgba( 255, 255, 255, .7);
}
form > table > tbody > tr > td {
    padding: 15px;
}
button {
    background-color: #4488cc;
    border: none;
    color: white;
    padding: 12px 25px;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    font-size: 16px;
}
button:hover {
    cursor: pointer;
    background-color: #6699ee;
}
#selection {
    margin: 0 10px;
    width: 300px;
    padding: 5px 15px;
    border: 1px solid #aaa;
    background-color: #fff;
}
#searchMe {
    margin: 0 10px;
    width: 300px;
    padding: 5px 15px;
    border: 1px solid #aaa;
    background-color: #fff;
}
td {
    padding: 5px 15px;
    margin: 0;
    vertical-align: text-top;
    font-size: 1.1em;
}
th {
    border: none;
    padding: 15px;
    margin: 0;
}
}
