css:
@-moz-document domain("bb.uhasselt.be") {
.appTabs td {
    padding-bottom: 0px !important;
}

.appTabs a {
    padding: 10px;
}

div.brandingImgWrap {
    height: 14px;
}

div.brandingImgWrap > a {
    top: 9px;
}

body {
    background-image: none !important;
    background-color: #333;
}

body.container {
    background-color: inherit !important;
}

.stream-detail-page {
 	 background-color: #dcdad5 !important;
}

@media (min-width: 1024px) {
    div#globalNavPageNavArea {
        height: 65px;
    }
    
    div#globalNavPageContentArea {
        height: auto !important;
    }
    
    .topTabs .tabWrapper-right {
        height: 48px;
    }
}
}

@-moz-document url-prefix("https://bb.uhasselt.be/bbcswebdav/") {
body {
    background-color: white !important;
}
}
