css:
@-moz-document domain(web.telegram.org) {

	.tg_head_peer_title{
      font-weight: 300; 
    }

    a {
      color: #46bc99;
    }
  
    .tg_head_split {
      color: #333 !important;
      background-color: #2d2c2c !important;
    }

    body{
      background-color: #2d2c2c;
    }
  
    .im_page_wrap{
      color: #ccc !important;
      background-color: #383736;
      border-left: 1px solid #2d2c2c;
      border-right: 1px solid #2d2c2c;
      border-top: none !important;
      border-bottom: none !important;
    }

    .im_dialogs_col_wrap{
      color: #ccc !important;
      background-color: #383736 !important;
      border-right: 1px solid #2d2c2c;
    } 

    .im_history_col_wrap{
      background-color: #434141 !important;
    }  
    
    .im_message_author {
      color: #999 !important;
    }

    .im_message_contact_photo, .im_message_from_photo, .im_message_fwd_photo {
          border-radius: 25%;
    }

    .im_dialog_peer{
		color: #ccc;
    }
    
   .im_history_message_wrap:hover{
      background-color: #403e3e !important;
   }
  
    .im_dialog_wrap:hover{
		background-color: #2d2c2c !important;
    }
}
