css:
@-moz-document domain("www.okcupid.com"), domain("okcupid.com") {

/* removes photos from site. */
div.quickview-top-photo,
a.HomeMatchCard-thumb-anchor,
span.nav-user-image-thumb {
	background:#FFF !important;
}
div#userRows-app img,
div.user_image,
div.mboxthread-photo,
div.actionbar2015-thumb,
div.similar2015-users img,
a.image_link img,
div.image,
div.qm-yml-users img,
div.userinfo2015-thumb,
img.HomeMatchCard-thumb-img,
div.quickview-top-photo img.active,
figure.TimelineEntry-thumb,
a.TimelineEntry-photo,
ul#messages img,
span.nav-user-image-thumb img,
.qmqueue-thumb img,
li.from_me img,
li.to_me img, 
div#event_display_body img {
	visibility:hidden !important;
}

a.image_link span.image_wrapper.loaded {
	opacity:0 !important;
}

div.qm-yml-users a,
div.similar2015-users a {
	background:gray !important;
}

div#mutual_match_modal .content,
div.morePhotos2015.profilesection,
div.qmcard-carousel-viewport-inner {
	display:none !important;
}

.i-checkmark:before,
.i-replied:before {
	color:green !important;
}

div.qm-content div.qmstack {
	margin-bottom:-200px !important; 
}

/* Removes match %, personality traits and questions */
span.userinfo2015-basics-asl-location + span.userinfo2015-basics-asl-spacer,
span.userinfo2015-basics-asl-match,
div.match-results-cards div.percentages,
div.quickview-top-percentage,
ul#thread .percentages,
div#userRows-app .userrow-percentage {
    visibility:hidden !important;
}

aside#improve_your_matches,
div.qvtraits,
div.actionbar2015-info-match,
div.qgenres2015,
div.traits2015,
div.cardsummary-match {
    display:none !important;
}

}
