css:
@-moz-document domain("twitter.com") {
.conversations-enabled .dismissible-content[data-you-follow="false"] {
    /*    		  background-color: #ff7777 !important;*/
    
    display: none;
}
.my-tweet,
.ProfileTimeline .dismissible-content[data-you-follow="false"] {
    display: block !important;
}
.DashboardProfileCard-avatarImage {
    border: 2px solid #17bf63;
}
.module.wtf-module.js-wtf-module.roaming-module.has-content {
    display: none
}
.AdaptiveMediaOuterContainer,
.AdaptiveMediaOuterContainer img {
    max-width: 100px;
    height: auto;
    top: 0 !important
}
.js-translate-tweet {
    display: none
}
.dashboard.dashboard-right {
    display: none
}
.content-main {
    width: 880px;
}
}
