/* UNREAD */
.topic_read_unlocked, .sticky_unread_mine, .topic_unread_hot, .topic_unread_hot_mine, .forum_unread, .pm_unread, .topic_unread {
    background-image: url(https://medias.wefrag.com/images/forum/unread.gif);
	background-size: 20px !important;
}
/* READ */
.sticky_read_mine,.topic_read_locked, .sticky_read,.topic_read_hot, .topic_read_hot_mine, .topic_read_mine, .pm_read, .topic_read, .forum_read, .topic_read_mine {
    background-image: url(https://medias.wefrag.com/images/forum/read.gif);
        background-size: 20px !important;
}
/* LOCKED */
.topic_unread_locked,.topic_read_locked {
    background-image: url(https://medias.wefrag.com/images/forum/disabled.gif);
        background-size: 20px !important;
}



