@-moz-document regexp("http://getcryptonews\\.com/index.php(.*)taigachat/popup"), regexp("https://getcryptonews\\.com/index.php(.*)taigachat/popup") {
body,
html {
    background-color: #1e2326
}
body {
    color: #eee;
    min-height: 100%!important;
    margin: 0 auto!important;
    border-left: 2px;
    border-right: 2px;
    border-bottom: 0;
    border-top: 0;
    border-style: solid;
    border-color: #282e31;
    background-color: #969696;                            
}
.ugc span {
    background-color: white;
}
#taigachat_inchat {
    font-style: italic;
    color: white;
}
#taigachat_box { height: 300px; border: 1px solid #a5cae4; padding: 5px; margin: 5px -2px 0 -2px; overflow: auto; overflow-y: scroll; font-size: 11px; background-color: #f0f7fc; -webkit-text-size-adjust: none; }
.taigachat_reverse #taigachat_box { margin-bottom: 5px; margin-top: 0; }
.taigachat_box li { /*background: #f0f7fc;*/ margin-bottom: 6px; }
.taigachat_controls { text-align: left; padding: 0 0 8px 0; display: table; width: 100%; }
.taigachat_toolbar, #taigachat_toolbar > * { vertical-align: top; }
.taigachat_message { width: 100%; box-sizing: border-box; -ms-box-sizing: border-box; }
.taigachat_message_holder { overflow: auto; padding-right: 5px; }
.taigachat_messagetext { word-wrap: break-word;
    max-width: 100%;
    background-color: black;}
.taigachat_reverse  { background-color: black;}
.taigachat_reverse #taigachat_box {
    margin-bottom: 5px;
    margin-top: 0;
    background-color: black;}

