    margin-bottom: 5px;
    margin-top: 0;
    background-color: black;}
.primaryContent a {
    color: #65a5d1;
}
.taigachat_activity {
    color: #6cb2e4;
    font-style: italic;
}
.nodeList .categoryStrip .nodeTitle a {
    color: #f9bc6d;
}
.nodeList .categoryStrip .nodeDescription {
    font-size: 10px;
    color: #c19966;
}
.discussionList .sectionHeaders a {
    display: block;
    color: #f9bc6d;
    outline: none;
}
.button {
    background: #66a0c7!important;
}
