css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

/* Hover Color uMatrix Button */
#umatrix_raymondhill_net-browser-action {
    list-style-image: url("chrome://mozapps/skin/extensions/alerticon-info-negative.svg") !important;
    background-position: center !important;
    background-repeat: no-repeat !important;
    background-image: none !important;
    filter:opacity(100%) !important;
}
#umatrix_raymondhill_net-browser-action:hover,
#umatrix_raymondhill_net-browser-action:active, 
#umatrix_raymondhill_net-browser-action[open] {
    list-style-image: url("chrome://mozapps/skin/extensions/alerticon-warning.svg") !important;
    filter: opacity(100%) hue-rotate(-30deg) saturate(500%) !important; 
    background: none !important;
    -moz-transition-duration: 0.5s !important; 
    -moz-transition-timing-function: ease-in !important; 
}
#umatrix_raymondhill_net-browser-action .toolbarbutton-badge-stack {
    background: none !important;
    border: none !important;
    -moz-transform: scale(1.1) !important; 
}
