css:
@-moz-document domain('lightnovelbastion.com')
{
  body, .wrapper.style2, .wrapper.style3, .wrapper.style4, div.wrapper, .box {
      background: rgb(72, 72, 72) !important;
  }
  p, ul, ol, dl, table {
      margin-bottom: 2em;
      color: #b2b2b2;
      text-shadow: none;
  }
  p, ul, ol, dl, table, div span span, b, strong, h1, h2, h3, h3 a, h5, h5 em, h6, ul.new-list a, ul.new-list a em, time.timeago,
  ul.reading-list>li>a, ul.reading-list>li.active>a>span, ul.reading-list>li.active>a>em {
      font-weight: 500 !important;
      color:#ededed !important;
      text-shadow: none !important;
  }
  ul.new-list>li.active a:hover, time.timeago:hover, ul.new-list a em:hover, strong:hover, 
  ul.new-list>li>a:hover, ul.reading-list>li.active a:hover {
      background: #91adc3 !important;
      color: black !important;
  }
  article .container {
      background: rgb(72, 72, 72);
  }
}
