@-moz-document domain("www.tumblr.com") {
/*                                                         MADE BY @persephone on stylish
	                                                      DO NOT STEAL THANKS!!! 
*/

/**                   ----------------------------------------- Circle Avatars -----------------------------------------                   **/

.post_avatar.post-avatar--sticky .post_avatar_link,
.post_avatar .post_avatar_link,
.blog-list-item .blog-list-item-avatar-image,
.avatar_img,
.radar .radar_avatar img,
.follower .avatar,
.is_tumblelog .avatar,
.pixelated-avatar,
.messaging-inbox .inbox-row .avatar,
.messaging-conversation--open .conversation-icebreakers .avatar img,
.blog-selector-container .selected-blog .avatar,
.messaging-conversation--open .conversation-message .avatar canvas,
.messaging-conversation--open .conversation-message .avatar img,
.post-composer_note-post .avatar img,
.post_full.is_note .avatar img,
.reblog-list-item .reblog-avatar,
.reblog-list-item .reblog-avatar .reblog-avatar-image-thumb,
.post_avatar .post_sub_avatar, 
.ui_notes .avatar, 
.ui_notes .avatar .inner_frame,
.post-activity-note-avatar .post-activity-avatar-link img{
    border-radius: 50%!important;
}

/**                   ----------------------------------------- Circle Avatars -----------------------------------------                   
	                                        If you have the NewXkit Tumblr Crushes Extension
**/

.crushes .crush {
    border-radius: 50%;
}
.crushes .crush .count {
    display: inline-block;
    position: relative;
    left: 35%;
    top: 65%;
    background: #8f8f8f;
}
}
