css:
@-moz-document domain("webm.land") {
body {
	background-color: #000000;
	color: #ccc;
}
header {
	background-color: #000000;
}
nav {
	background-color: #111111;
}
nav ul li.active a, nav ul li a:hover, li:hover li a:hover {
	background-color: #222;
}
nav ul li a {
	color: #aaaaaa;
}
section.content .col img {
	border: 1px solid #333;
}

.pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus {
	color: #ababab;
	background-color: #050505;
	border-color: #333;
}
.pagination > li > a, .pagination > li > span {
	color: #8474a1;
	background-color: #050505;
	border: 1px solid #333;
}
.pagination > .active > a, .pagination > .active > span, .pagination > .active > a:hover, .pagination > .active > span:hover, .pagination > .active > a:focus, .pagination > .active > span:focus {
	background-color: #1a1228;
	border-color: #333;
}
.pagination > li > a:hover, .pagination > li > span:hover, .pagination > li > a:focus, .pagination > li > span:focus {
	color: #ffffff;
	background-color: #101010;
	border-color: #333;
}
h2 {
	border-bottom: 1px solid #333;
}
#idc-container .idc-foot {
	border-top: 1px solid #333;
}
section.filter a.active, section.filter a:hover, section.filter a:active {
	color: #ccc;
}
section.filter a {
	color: #999999;
}

#idc-container div.idc-text_noresize, #idc-container textarea.idc-text_noresize,
#idc-container .idc-text {
	background: #101010;
	border: 1px solid #333;
	border-top: 1px solid #444;
	color: #fff!important;
}
input, textarea, select {
	border: 1px solid #333;
	color: #ababab;
	background: #101010;
}

.report > button {
	background: #222;
	border-color: #222;
}
a, .report > button > a, #idc-container a{
	color: #3baeff;
}

#idc-container .idc-head_tools-login span,
#idc-container .idc-head_tools-share,
#idc-container .idc-btn_l span, #idc-container .idc-btn_s span{
	filter: invert(1);
}

.modal .modal-body {
	background-color: #111;
}
.modal .modal-footer {
	background-color: #151515;
}
}
