css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reports.cubecraft.net"), domain("appeals.cubecraft.net") {
    body{
        background-image: url('http://cdn.wonderfulengineering.com/wp-content/uploads/2016/01/black-wallpaper-3.jpg');
        background-repeat: no-repeat;
    }
    
    /*font color*/
    body, .panel-default > .panel-heading, a:hover {
        color:#aaaaaa;
    }
    
    /*scrolling header*/
    #header, #cubecraft-store-submenu {
        position: absolute;
    }
    
    /*normal background*/
    .panel-default, .panel-default > .panel-heading {
        background-color: #0e0e0e;
    }
    
    /*lighter background*/
    .list-group-item, .pagination > li > a, .pagination > .disabled > span, .form-control, .table-striped > tbody > tr:nth-of-type(2n+1) {
        background-color: #353535;
    }
    
    /*light grey border*/
    .panel-default, .table-responsive > .table, .form-control, .panel-default > .panel-heading {
        border-color: #323232;
    }
    
    /*dark grey border*/
    .list-group-item, .pagination > .active > span, .pagination > li > a, .pagination > .disabled > span {
        border-color: #0e0e0e;
    }
    
    /*dark green box*/
    .alert-success {
        color: #d6e9c6;
        background-color: #15250e;
        border-color: #18250e;
    }
}
