@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("telegra.ph") {
  body {
    background: #000;
    color: #fff;
  }
  
  .tl_article .tl_article_header, .tl_article .tl_article_content .ql-editor {
    color: #fffc;
  }
  
  .tl_article .tl_article_content aside {
    color: #fffa;
  }
  
  .tl_article a[href] {
    border-bottom: .1em solid #fffc;
  }
}
