css:
@-moz-document domain("byui.brightspace.com") {
/* Change all font-size to 15 px*/
html {
    font-size: 15px !important;
}
/* Fix header on top */
header {
    font-size: 0.9em;
    z-index: 999;
    position: fixed;
    display: block;
    width: 100%;
}
/* Move down left and main panel */
.d2l-page-main-padding,
.d2l-page-collapsepane-container,
.d2l-twopanelselector-wrapper {
    padding-top: 90px !important;
}
div#topicListCollapsePane {
    top: 135px;
}

/* Reduce size of header container */
.d2l-navigation-s-header-container {
    height: 40px;
}
/* Reduce size of header icons*/
d2l-icon {
    height: 20px !important;
    width: 20px !important;
}
.d2l-navigation-s-header-container .d2l-navigation-s-logo-link img {
    height: 35px; 
}
/* Reduce size of navigation arrow*/
.d2l-navigation-s-group-wrapper .x-scope {
    height: 10px !important;
    width: 10px !important;
}
/* Reduce size of navbar */
.d2l-navigation-s-main-wrapper {
    height: calc(1rem + 20px);
}
.daylight .d2l-le-TreeAccordionItem-wrapper {
    padding: 8px 30px 8px 14px;
    margin: -18px -30px -18px -14px;
}
/* Reduce size of shadow gradient */
.d2l-navigation-s-shadow-gradient {
    height: 20px;
    bottom: -20px
}
/* Disscussions (Show complete post and hide "Show more" button) */
.more-less-content {
    height: auto !important;
}
.more-less-toggle {
    display: none !important;
}
/* Links visited */
 .d2l-link:visited {
    color: gray !important;
}
/* Change images to a fixed size */
.d2l-htmlblock-untrusted img,
.d2l-htmlblock img {
    max-width:700px !important;
    height: auto !important;
    display: block;
    margin: 10px 0;
}
/* Correct spacing in settings and help icons */
d2l-icon.d2l-button-subtle-icon.d2l-button-subtle {
    left:0px;
}
/* Fix notification icon size */
.d2l-navigation-s-notification-indicator d2l-icon {
    width:10px !important;
}
}
