css:
@-moz-document domain("www.reddit.com") {
:root	{
 --bg-color: #1E1E1E;
 --sidebar-color: #222222;
 --page-divider: #1A1A1A;
 --comment-focus-bg: #292929;

}

.res-filterline {
    background-color: var(--bg-color);
}

.RES-keyNav-activeElement, .RES-keyNav-activeElement .md-container {
    background-color:   var(--comment-focus-bg) !important;
}

.titlebox form.toggle, .leavemoderator {
    background: var(--sidebar-color) none no-repeat scroll center left;
}

.NERPageMarker {
    color: rgb(127, 127, 127);
    background-color: var(--bg-color);
    border-color: var(--bg-color);
}

#progressIndicator {
    background-color: var(--bg-color);
    border-color: var(--bg-color);
}

a.voteWeight {
    background-color: transparent !important;
}



.res .RESUserTagImage {
		background-image: None;
}
}
