css:
@-moz-document domain("hfboards.mandatory.com") {
body, html {
  background:#333;
  color:#000000;
}

/*Page Background*/

#content .pageContent {
    background: #e0e0e0 ;
    max-width: 1900px;
    margin: 0 auto;
}

/*Main Forum Page*/

ol.discussionListItems li {
    background: #efefef;
    border-bottom: rgba(0,0,0,.20);
}

ol.discussionListItems li:nth-child(odd) {
    background: #e2e2e2;
}

/*Inside Thread*/


ol.messageList li .primaryContent {
    background: #efefef;
    
}
ol.messageList li:nth-child(odd), .primaryContent.NewsFeedItem:nth-child(odd), ol.alertsScroller li:nth-child(odd)  {
    background: #e2e2e2;
}
/*Header*/

#logoBlock, a.currentPage, .PageNav a:hover, a.callToAction span, .navTabs .navTab.PopupOpen .navLink:hover, .PageNav a.currentPage, .button.primary, .navTabs .navTab.Popup.PopupControl.PopupOpen:hover, .Popup.PopupContainerControl.PopupOpen, .navTabs .navTab.Popup.PopupContainerControl.PopupOpen, .navTabs .publicTabs .navTab.PopupClosed:hover,
.heading, .xenOverlay .formOverlay .heading, .subHeading  {
    background: #000000;
}
#header {
  background:rgb(253, 185, 45);
}
}
