css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.kropyva.ch") {
  body, div.post.reply, div.post.reply.highlighted {
    background: #282B2C;
    color: #babdb6;        
  }
  
  div.post.reply {
    border: 2px solid #babdb6;
  }
  
  p.intro span.subject {
    color: #ddd;
  }
  
  div.post.reply div.body a, a, .intro a, a:hover, a:visited {
    color: burlywood;
  }

  div.post.op span.spoiler, div.post.reply span.spoiler {
      background: darkgrey;
      color: darkgray;
  }

  div.boardlist:nth-child(1), div.boardlist div.pages {
    background: darkgray;
  }

  body>div.pages, .webm-settings div {  
    background: darkgray !important;
    color: #e8e8e8;
  }

  div.pages a {
    color: #e8e8e8;
  }

  div.pages a.selected {
    color: burlywood;
  }

  img {
    filter: grayscale(0.5);
    transition: 200ms;
  }

  img:hover {
    filter: grayscale(0);
  }
}
