css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.animeplus.tv"), url("http://www.animeplus.tv/"), domain("animeplus.tv")
{

/* Ads */
#upper_header .right_col, 
#content>[style="border-collapse: collapse"], 
#content>[align="center"], 
.sc_ad,
.ad,
#rcjsload_bbc25d, 
#rcjsload_79dccc, 
#BB_SK_7397343889, 
body>.right_col, 
#body>.right_col, 
body>div:nth-of-type(2), 
#manga_releases, 
.mcimg  
#content>#g_promo,
iframe+table[width="745"],
iframe[width="742"][height="195"],
#downloads>table[width="720"],
div[style="width:336px;height:768px;margin:auto;"],
div[align="center"]>table[width="740"],
.ad>iframe,
#mini-announcement,
.right_col>iframe,
#ad_unit>iframe,
.safeuploada-content, 
iframe>#MarketGidLuxupDiv16180,
.MarketGidComposite16180,
.note {
    visibility: hidden!important;
    display: none!important;
}


/* Layout */
.vmargin {
    margin-left:0!important;
    padding-left:35px!important;
    display: inline-block!important;
}

div>iframe, .vmargin {
    width:818px!important;
    height:500px!important;	
}

#body>div {
    width: auto!important;
}






/* Background */
#upper_header div .left_col div {
    background: url(https://i.imgur.com/Tb9cJjF.png) no-repeat;
}

#page, #header {
    background: linear-gradient(to right, #e2e5eb 35%, #ffffff 40%);
}

#streams, #top_block {
/*     background: linear-gradient(to bottom right, #365089 20%, #ffffff 50%); */
    background: radial-gradient( #ffffff, #365089 95%);
}

#top_block {
    background: linear-gradient(to  right , #e2e5eb 91%, #ffffff 100%);
}




/* BODY FONTS */
/* VISITED */
#body a:visited {
    color: crimson!important;
    font-weight:bold;
}

/* HOVER */
#body a:hover {
    color: #FF0000!important;
}


















/* Stream Background */
#streams div:nth-child(2) div:nth-child(1) {
    background: #3E3E3E;
}
#streams div:nth-child(3) div:nth-child(1) {
    background: #3E3E3E;
}
#streams div:nth-child(4) div:nth-child(1) {
    background: #3E3E3E;
}
#streams div:nth-child(5) div:nth-child(1) {
    background: #3E3E3E;
}


}
