css:
@-moz-document url-prefix("http://ninjakiwi.com/Games"),
url-prefix("https://ninjakiwi.com/Games") {
    #international, #disclaimer, #footer, #secondary, .show-header, .header-bar, .addthis_toolbox, #_atssh, .fb-like {
        display: none !important;
    }
    #content {
        background: none !important;
        box-shadow: none !important;
    }
    #game {
        position: fixed !important;
        top: 0 !important;
        left: 0 !important;
        height: 100% !important;
        width: 100% !important;
    }
    #preroll {
        position: fixed !important;
        left: 0;
        top: 0;
        width: 100%;
        height: 100%;
    }
}
